import React, { Component } from 'react';
import Todos from './components/Todos';
import './App.css';

class App extends Component {
  state  = {
    todos: [
      {
        id: 1, 
        title: "Make my Resume",
        isCompleted: false,
      },
      {
        id: 2, 
        title: "Go to the gym",
        isCompleted: false,
      },
      {
        id: 3, 
        title: "Have dinner with friends",
        isCompleted: false,
      }, 
      {
        id: 4, 
        title: "Cooking with friends",
        isCompleted: false,
      }
    ],
  }
  isCheckedComplete = (id)=>{
    this.setState({ todos: this.state.todos.map( (todo) =>{
      if( todo.id  === id) {
        todo.isCompleted = !todo.isCompleted;
      }
      return todo
    })});
    console.log(id);
}
  render() {
    return (
      <div className="App">
        <Todos todos = { this.state.todos} isCheckedComplete = { this.isCheckedComplete }/>
      </div>
    );
  }
}

export default App;
