import React, { Component } from 'react'

class AddTodo extends Component {
    state = {
        title: ''
    }

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

onSubmit = (e)=>{
    e.preventDefault();

        this.props.addTodo(this.state.title);
    this.setState({
        title: ''
    })



    }
  render() {
    return (
      <form onSubmit = { this.onSubmit}>
        <input
        style = { styles.inputStyle}
        type = 'text'
        name = 'title'
        placeholder = 'Add Task'
        value = { this.state.title}
        onChange = { this.onChange}
        />
        <input 
        style = { styles.addBtStyle}
        type = 'submit' 
        value =' Add' />

      </form>
    )
  }
}

const styles = {
    inputStyle:{
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    width: '200px',
    fontSize: '18px',
    marginTop: '20px',
    marginBottom: '20px',
    outline: 'none',
    borderRadius: '22px',
    padding: '20px',
    paddingTop: '5px',
    paddingBottom: '5px',
},
addBtStyle:{
    backgroundColor: '#ff0057',
    color: 'white',
    fontSize: '18px',
    width: '80px',
    outline: 'none',
    padding: '10px',
    borderRadius: '45px',
}
}

export default  AddTodo;
