import React, { Component } from 'react';
import Todos from './components/Todos';
import './App.css';
import Header from './components/Header';

import AddTodo from './components/AddTodo';

import uuid from 'uuid';

const url = "./sound/blip-plock-pop.mp3";
const audio = new Audio(url);

class App extends Component {
  state  = {
    todos: [
      {
        id: uuid.v4(), 
        title: "Make my Resume",
        isCompleted: false,
      },
      {
        id: uuid.v4(), 
        title: "Go to the gym",
        isCompleted: false,
      },
      {
        id: uuid.v4(), 
        title: "Have dinner with friends",
        isCompleted: false,
      }, 
      {
        id: uuid.v4(), 
        title: "Cooking with friends",
        isCompleted: false,
      }
    ],
    isMusicPlaying: false 
  }
  isCheckedComplete = (id)=>{

    this.setState({ todos: this.state.todos.map( (todo) =>{
      if( todo.id  === id) {
        todo.isCompleted = !todo.isCompleted;
      }
      audio.play()
      console.log(" audio + ", audio)
      return todo
    })});
}

deleteTask = (id)=>{
  console.log("hello there " + id)
  this.setState({
    todos: [...this.state.todos.filter( todo => todo.id !== id)]
  });
  audio.play()
}

addTodo = ( title) =>{
  const newTask = {
    id: uuid.v4(), 
    title, 
    isCompleted: false
  }
  this.setState({ todos: [...this.state.todos, newTask]})
  audio.play()
}
  render() {
    return (
      <div className="App">
        <Header/>
        <AddTodo addTodo = { this.addTodo }/>
         <div style = { styles.todoContainer}>
         <Todos todos = { this.state.todos} isCheckedComplete = { this.isCheckedComplete } deleteTask = { this.deleteTask} />
         </div>

      </div>
    );
  }
}
const styles = {
  todoContainer: {
    backgroundColor: 'blue'
  }
}
export default App;
