import React, { Component } from 'react';
import Todos from './components/Todos';
import './App.css';
import Header from './components/Header';

import AddTodo from './components/AddTodo';

import uuid from 'uuid';

import {PieChart} from 'react-easy-chart';

//const url = "./sound/blip-plock-pop.mp3";
//const audio = new Audio(url);

class App extends Component {
  state  = {
    todos: [
      {
        id: uuid.v4(), 
        title: "Make my Resume",
        isCompleted: false,
      },
      {
        id: uuid.v4(), 
        title: "Go to the gym",
        isCompleted: false,
      },
      {
        id: uuid.v4(), 
        title: "Have dinner with friends",
        isCompleted: false,
      }, 
      {
        id: uuid.v4(), 
        title: "Cooking with friends",
        isCompleted: false,
      }
    ],
    numberOfCompeletedTask: 0,

    //isMusicPlaying: false 
  }
  isCheckedComplete = (id)=>{

    this.setState({ todos: this.state.todos.map( (todo) =>{
      if( todo.id  === id) {
        todo.isCompleted = !todo.isCompleted;
      }

      //audio.play()
      //console.log(" audio + ", audio)
      return todo
    })});
}

deleteTask = (id)=>{
  //console.log("hello there " + id)

  const num1 = this.state.numberOfCompeletedTask + 1;
      console.log("num1" + num1);
  this.setState({
    todos: [...this.state.todos.filter( todo => todo.id !== id)]
  });
  const n = this.state.todos.length -1
  console.log("lenth  = " + n);
  //audio.play()
}

addTodo = ( title) =>{
  const newTask = {
    id: uuid.v4(), 
    title, 
    isCompleted: false
  }
  this.setState({ todos: [...this.state.todos, newTask]})
  const n = this.state.todos.length + 1
  console.log("lenth  = " + n);
  //audio.play()
}
  render() {
    return (
      <div className="App">
        <Header/>
        <div>
        <AddTodo addTodo = { this.addTodo }/>
        </div>

         <div style = { styles.dashboard} >
         <div style = { styles.todoContainer}>
         <Todos todos = { this.state.todos} isCheckedComplete = { this.isCheckedComplete } deleteTask = { this.deleteTask} />
         </div>
         <div>
           <h4> Productivity</h4>
           <PieChart
           size={200}
           innerHoleSize={100}
           labels
    data={[
      { key: 'Completed', value: 100, color: 'rgba(255, 255, 255, 0.5)' },
      { key: 'Task', value: 260, color: 'rgba(5, 5, 5, 0.2)' },
    ]}
  />
  <h5> 98 % productive</h5>
  </div>
         </div>

      </div>
    );
  }
}
const styles = {
  dashboard:{
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
  },
  todoContainer: {
    backgroundColor: 'rgba(255, 255, 255, 0.2)', 
    width : '100%',
    padding: '10px',
    justifyContent: 'center',
    borderRadius: '30px',
    marginRight: '40px',
  }
}
export default App;
